import { useEffect, useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios";

function UpdateDoctorProfile() {
    const Home=()=>{
        history.push('/DoctorHome');
      }
      const history=useHistory();
      const [error,setError]=useState("");
      const [success,setSuccess]=useState("");
      const [selectedImage, setSelectedImage] = useState(null);

      const id= window.sessionStorage.getItem("userId");

      const [doctorDetails,setDoctorDetails]=useState({
                                                          firstName: "",
                                                          lastName: "",
                                                          gendeer: "",
                                                          email: "",
                                                          mobNumber: "",
                                                          password: "",
                                                          addressLineOne: "",
                                                          addressLineTwo: "",
                                                          experience: 0,
                                                          speciality: "",
                                                          charges: 0,
                                                          id: 0
                                                        });

      var inputTxt=(args)=>{
        //    debugger; 
            var copy={...doctorDetails};
            copy[args.target.name]=args.target.value;
            setDoctorDetails(copy)  

            }

//http://localhost:7070/doctor
    useEffect(()=>{
                var helper=new XMLHttpRequest;
                helper.onreadystatechange=()=>{
                    if(helper.readyState==4&&helper.status==200){
                        var data=JSON.parse(helper.responseText);     
                        console.log(data);
                        setDoctorDetails(data);
                    }
                };
                helper.open('GET','http://localhost:7070/doctor/{did}?hID='+id);
                helper.setRequestHeader("Content-Type","application/json");
                helper.send();
    },[]);

      var addDoctor=()=>{
        var helper=new XMLHttpRequest();
          helper.onreadystatechange=()=>{
             // debugger;
              if(helper.status===201&&helper.readyState===4){
                 //console.log("data added successfully");
                 setSuccess("data added successfully");
                  setDoctorDetails({
                    firstName: "",
                    lastName: "",
                    gendeer: "",
                    email: "",
                    mobNumber: "",
                    password: "",
                    addressLineOne: "",
                    addressLineTwo: "",
                    experience: 0,
                    speciality: "",
                    charges: 0,
                    id: 0
                  });
                  toast.success("Updated Successfully!!!", {
                    position: "top-center",
                    autoClose: 1000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                  });
                  window.setTimeout(()=>{
                    setSuccess("");
                    history.push('/DoctorHome');
                },2000);
              }
          }
          helper.open("PUT","http://localhost:7070/doctor");
          helper.setRequestHeader("Content-Type","application/json")
          helper.send(JSON.stringify(doctorDetails));
      }

 const uploadImage=(event)=>{
   event.preventDefault();

   const formData = new FormData();
   formData.append("imageFile", selectedImage);

   axios
     .post("http://localhost:7070/doctor/images/"+id, formData)
     .then((result) => {

         console.log(result);
         toast.success("Image Added Successfully!!!", {
           position: "top-center",
           autoClose: 1000,
           hideProgressBar: false,
           closeOnClick: true,
           pauseOnHover: true,
           draggable: true,
           progress: undefined,
         });
     })
     .catch(error => {
       toast.error("Image Uploading failed!!!", {
         position: "top-center",
         autoClose: 1000,
         hideProgressBar: false,
         closeOnClick: true,
         pauseOnHover: true,
         draggable: true,
         progress: undefined,
       });
         });
       }


      return ( 
          <>
          <nav class="navbar navbar-expand-lg bg-body-tertiary rounded" aria-label="Thirteenth navbar example">
        <div class="container-fluid">
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarsExample11" aria-controls="navbarsExample11" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse d-lg-flex" id="navbarsExample11">
            {/* <a class="navbar-brand col-lg-3 me-0" href="#">Home</a> */}

            <div class="navbar-brand col-lg-1 me-0 btn" aria-current="page" onClick={Home}>Home</div>
            <div class="navbar-brand col-lg-1 me-0" aria-current="page"></div>
