import { useEffect, useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min"

function PatientHome() {

    const history=useHistory();
    const [patientDetails,setPatientDetails]=useState({
                                                        firstName: "",
                                                        lastName: "",
                                                        gendeer: "",
                                                        email: "",
                                                        mobNumber: "",
                                                        password: "",
                                                        addressLineOne: "",
                                                        addressLineTwo: "",
                                                        id: 0
                                                      });
    const [hospitalDetails,sethospitalDetails]=useState([]);
    const id= window.sessionStorage.getItem("userId");
    const [hid,setHid]=useState(0);
    const [doctorDetails,setDoctorDetails]=useState([{
      firstName: "",
      lastName: "",
      gendeer: "",
      email: "",
      mobNumber: "",
      addressLineOne: "",
      addressLineTwo: "",
      experience: 0,
      speciality: "",
      charges: 0,
      id: 0
    }]);

    const Home=()=>{
        history.push("/");
    }

    const Appointments=()=>{
        history.push('/PatientAppointments');
    }

    const TakeAppointments=()=>{
        history.push('/TakeAppointments');
    }

    const UpdateProfile=()=>{
        history.push('/PatientProfile');

    }

    const Logout=()=>{
        sessionStorage.removeItem("userName");
        history.push("/");
    }

    const GotoDetails=(id)=>{
      window.sessionStorage.setItem("hId",id);
      history.push('/ViewHospital');
  }

  const add=()=>{
    var helper=new XMLHttpRequest;
        helper.onreadystatechange=()=>{
            if(helper.readyState==4&&helper.status==200){
                var data=JSON.parse(helper.responseText);     
                //console.log(data);
                setPatientDetails(data);
            }
        };
        helper.open('GET','http://localhost:7070/patient/{id}?id='+id);
        helper.setRequestHeader("Content-Type","application/json");
        helper.send();
};


  useEffect((id)=>{
      var helper=new XMLHttpRequest;
    helper.onreadystatechange=()=>{
        if(helper.readyState==4&&helper.status==200){
            var data=JSON.parse(helper.responseText);     
            //console.log(data);
            setDoctorDetails(data);
        }
    };
    helper.open('GET','http://localhost:7070/doctor/hospital/{hid}?hID='+hid);
    helper.setRequestHeader("Content-Type","application/json");
    helper.send();
},[doctorDetails]);

useEffect(()=>{
  var helper=new XMLHttpRequest;
  helper.onreadystatechange=()=>{
      if(helper.readyState==4&&helper.status==200){
          var data=JSON.parse(helper.responseText);     
          //console.log(data);
          sethospitalDetails(data);
          add();
      }
  };
  helper.open('GET','http://localhost:7070/hospital');
  helper.setRequestHeader("Content-Type","application/json");
  helper.send();
},[]);

// const loadDoctor=(i)=>{
//   var helper=new XMLHttpRequest;
//     helper.onreadystatechange=()=>{
//         if(helper.readyState==4&&helper.status==200){
//             var data=JSON.parse(helper.responseText);     
