//           helper.send(JSON.stringify(doctorDetails));
//       }



const updateAppointment=()=>{
  appointments.status="DONE"
  var helper=new XMLHttpRequest;
        helper.onreadystatechange=()=>{
        if(helper.readyState==4&&helper.status==200){
          toast.success("Appointment Added Successfully!!!", {
            position: "top-center",
            autoClose: 1000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
        window.setTimeout(()=>{
            history.push("/DoctorHome");
        },3000);
        }
        };
        helper.open('PUT','http://localhost:7070/appointment/done/{pid}?pid='+AppointmentId);
        helper.setRequestHeader("Content-Type","application/json");
        helper.send(JSON.stringify(appointments));

      }


//update    http://localhost:7070/appointment/done/{pid}?pid=14



var inputTxt=(args)=>{
  //    debugger; 
      var copy={...reciept};
      copy[args.target.name]=args.target.value;
      setReciept(copy)  

      }

useEffect(()=>{
        var helper=new XMLHttpRequest;
        helper.onreadystatechange=()=>{
        if(helper.readyState==4&&helper.status==200){
       var data=JSON.parse(helper.responseText);     
       setAppointments(data);
        }
        };
        helper.open('GET','http://localhost:7070/appointment/single/{aId}?aId='+AppointmentId);
        helper.setRequestHeader("Content-Type","application/json");
        helper.send();
      },[]);

    return ( 
        <>
        <nav class="navbar navbar-expand-lg bg-body-tertiary rounded" aria-label="Thirteenth navbar example">
      <div class="container-fluid">
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarsExample11" aria-controls="navbarsExample11" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>

        <div class="collapse navbar-collapse d-lg-flex" id="navbarsExample11">
          {/* <a class="navbar-brand col-lg-3 me-0" href="#">Home</a> */}

          <div class="navbar-brand col-lg-1 me-0 btn" aria-current="page" onClick={Home}>Home</div>
          <div class="navbar-brand col-lg-1 me-0" aria-current="page"></div>
          <div class="navbar-brand col-lg-1 me-0 " aria-current="page" ></div>

          <ul class="navbar-nav col-lg-6 justify-content-lg-center">

          </ul>

        </div>
      </div>
    </nav>
    <div>
      <div className="mt-2 d-flex aligns-items-center justify-content-center">
        <div
          className="card form-card border-color custom-bg"
          style={{ width: "25rem" }}
        >
          <div className="card-header bg-color custom-bg-text text-center">
            <h5 className="card-title">Appointment</h5>
          </div>
          <div className="card-body text-color">
            <form>
              <div className="mb-3">
                <label htmlFor="name" className="form-label">
                  <b>Patient Name</b>
                </label>
                <input
                  type="text"
                  className="form-control"
                  value={appointments.patientName}
                  disabled='none'
                />
              </div>
              <div className="mb-3">
                <label htmlFor="description" className="form-label">
                  <b>Problem Description</b>
                </label>
                <textarea
                  className="form-control"
                  id="problem"
                  name="problem"
                  rows="3"
                 value={appointments.diseases}
                 disabled='none'
                />
              </div>

              <div className="mb-3">
                <label htmlFor="name" className="form-label">
                  <b>Appointment Date</b>
                </label>
                <input
                  type="text"
                  className="form-control"
                 value={appointments.startDate}
                 disabled='none'
                />
              </div>

               <div className="mb-3">
                <label htmlFor="prescription" className="form-label">
                  <b>Prescription</b>
                </label>
                <textarea
                  className="form-control"
                  id="prescription"
                  name="prescription"
                  rows="3"
                  onChange={inputTxt}
                   value={reciept.prescription}
                />
              </div>

              <div className="mb-3">
                <label htmlFor="price" className="form-label">
                  <b>Treatment Price</b>
                </label>
                <input
                  type="number"
                  className="form-control"
                  name="treatmentPrice"
                   value={reciept.treatmentPrice}
                   onChange={inputTxt}
                />
              </div>

              <button
                type="submit"
                className="btn bg-color custom-bg-text"
                onClick={saveAppointment}
              >
                Update Appointment
                <ToastContainer></ToastContainer>
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
        </>
     );
}

export default DoctorReciept;
