import { useEffect, useState } from 'react';
import './MiddleLayer.css'

function MiddleLayer() {

    const [hospitalDetails,sethospitalDetails]=useState([]);

    const GotoDetails=()=>{

    }
    useEffect(()=>{
        var helper=new XMLHttpRequest;
        helper.onreadystatechange=()=>{
            if(helper.readyState==4&&helper.status==200){
                var data=JSON.parse(helper.responseText);     
                console.log(data);
                sethospitalDetails(data);
            }
        };
        helper.open('GET','http://localhost:7070/hospital');
        helper.setRequestHeader("Content-Type","application/json");
        helper.send();
    },[]);


    return ( 
        <> 
        <div className="container marketing" style={{marginTop:20}}>
                 <div className="row">
                 {
                    hospitalDetails.map((hospital,index)=>{
                        return <>
                                 <div className="col-lg-4">
                                    <img className="img-circle imgs"  src="http://localhost:3000/Imgs/hospital.jpg" alt="Generic placeholder image" width="140" height="140" id='101'  onClick={GotoDetails}/>
                                    <h2>{hospital.hospitalName}</h2>
                                    <div class="overflow-auto p-3 bg-light" style={{maxWidth : '100%', maxHeight: 150,overflowY: 'hidden',color:'gold'}}>
                                        <p>{hospital.hospitalInfo}</p>
                                    </div>
                                    <a className="btn btn-default" style={{margin:20}} href="#" role="button">View details &raquo;</a>
                                 </div>
                        </>
                    })
                }
            </div> 
           </div>
        </>
     );
}

export default MiddleLayer;
